class Solution {
    public int searchInsert(int[] nums, int target) {
        //查找target记录位置
        int status=Arrays.binarySearch(nums, target);

        //如果target不在数组内，则插入到
        if(status<0) {
            for (int i = 0; i < nums.length; i++){
                if (target<nums[i]) {
                    status=i;
