class Solution {
    public int findUnsortedSubarray(int[] nums) {
        int b[] =new int[nums.length];
         int variable=0,len=0;
         for (int i = 0; i < nums.length; i++) {
            b[i]=nums[i];
        }

         Arrays.sort(b);

         for (int i = 0; i < nums.length; i++) {
                if(b[i]==nums[i]) ++variable;
                else break;
            }
         if (variable==nums.length) variable=1;
        int j=0,count;//j：索引号
         //遍历数组a[],比较两值
         for (int i = 0; i < nums.length; ++i) {
             //为什么int i=j，j的值不能传递给i？
