                    count=0;
                    for (int k = 0; k < b.length; k++) {
                        if(nums[i]==b[k])count++;
                    }
                    count=count-1;
                    if(nums[nums.length-1]==nums[nums.length-2])count=count-1;
                    i=Arrays.binarySearch(b, nums[i])+count;
                    j=i;
                }
            }
         len=j+1-variable;
         return len;
    }
}




