public int[] twoSum(int[] numbers, int target) {
    int []num = new int[2];
    int value;
    for (int i = 0; i < numbers.length-1; i++) {
        value = target - numbers[i];
        if (Arrays.binarySearch(numbers,i+1,numbers.length,value) >i) {
            num[0] = i+1;
